import Link from "next/link";
import Head from "next/head";

export default function User({ user, posts }) {
  return (
    <>
      <Head>
        <title>User #{user.id}</title>
      </Head>
      <div>
        <div className="grid grid-template-columns gap-x-6 p-16"></div>
        <h1 className="text-2xl text-center text-black">{user.name}</h1>
        <h4 className="text-base text-center text-black">{user.email}</h4>
        <h4 className="text-base text-center text-black">
          {user.company.name}
        </h4>
        <h1>Posts: {posts.lenght}</h1>
        <div className="w-36 m-auto flex justify-center mt-16 bg-gray-30 rounded-xl cursor-pointer">
          <Link href="/users">
            <a>Back to users</a>
          </Link>
        </div>
        <style jsx>{`
          .grid-template-columns {
            grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
          }
        `}</style>
      </div>
    </>
  );
}

User.getInitialProps = async (context) => {
  try {
    const user = await (
      await fetch(
        `https://jsonplaceholder.typicode.com/users/${context.query.id}`
      )
    ).json();

    const posts = await (
      await fetch(
        `https://jsonplaceholder.typicode.com/posts?userId=${context.query.id}`
      )
    ).json();

    //https://jsonplaceholder.typicode.com/posts?userId=1
    return { user, posts };
  } catch (error) {}
};
